import React, {useState, useEffect} from 'react'
import BSForm from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import {createNote, getNotes, updateNote, deleteNote} from '../utils/noteHelpers'
import Alert from 'react-bootstrap/Alert'

/*
Requirements
1. (DONE) write text in the field and store it as a new note
2. (DONE) click on a note and displat the taxt in the fields.
3. (DONE) edithe field filled with text from a selected note
4. (DONE) Clicking save while on a selected note will update that selected note - how do ew make <List /> rerender when clicking save?
*/

const STATUS_INITIAL_VALUE = ''

export default function Form({selectedNote, setSelectedNote, refreshList}) {
  console.log(selectedNote)
  const [title, setTitle] = useState('')
  const [body, setBody] = useState('')
  const [status, setStatus] = useState(STATUS_INITIAL_VALUE)
  const [variant, setVariant] = useState('')

  //if (selectedNote) setTitle(selectedNote.title)

  useEffect(() => {
    if (selectedNote) return setTitle(selectedNote.title)
    setTitle('') && setBody(selectedNote.body)
    setBody('')
  }, [selectedNote])

  useEffect(() => {
    if (selectedNote) return setBody(selectedNote.body)
    setBody('')
  }, [selectedNote])

  useEffect(() => {
    setTimeout(() => setStatus(STATUS_INITIAL_VALUE), 3000)
  }, [status])

  useEffect(() => {
    if (selectedNote) return setVariant('')
  }, [variant])

  const onChangeTitle = (e) => setTitle(e.target.value)
  const onChangeBody = (e) => setBody(e.target.value)
  const onSave = (e) => {
    e.preventDefault()
    setTitle('')
    setBody('')
    setStatus('Saved!')
    setVariant('success')
    if (selectedNote) {
      updateNote(selectedNote.id, title, body)
      return refreshList()
    }

    console.log('Saving new note:', title)
    createNote(title, body)
    refreshList()
    console.log(getNotes())
  }

  const onDelete = (e) => {
    e.preventDefault()
    const {id} = selectedNote
    deleteNote(id)
    refreshList()
    setTitle('')
    setBody('')
    setStatus('Deleted!')
    setVariant('danger')
  }

  return (
    <BSForm>
      <BSForm.Group controlId="formGroupTitle">
        <BSForm.Label>Title</BSForm.Label>
        <BSForm.Control //value={selectedNote ? selectedNote.title : title} onChange={onChangeTitle}
          value={title}
          onChange={onChangeTitle}
        />
      </BSForm.Group>
      <BSForm.Group controlId="formGroupNote">
        <BSForm.Label>Note</BSForm.Label>
        <BSForm.Control as="textarea" value={body} onChange={onChangeBody} />
      </BSForm.Group>
      <Button onClick={onSave} variant="secondary" style={{marginRight: '5px'}}>
        Save
      </Button>
      {selectedNote && (
        <Button onClick={onDelete} variant="danger">
          Delete
        </Button>
      )}
      {selectedNote && (
        <Button onClick={onDelete} variant="">
          Archive
        </Button>
      )}
      {status && <Alert variant={variant}>{status}</Alert>}
    </BSForm>
  )
}

const styles = {
  button: {marginRight: 10},
}
