import React, {useState} from 'react'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import {createNote, getNotes} from '../utils/noteHelpers'

/*
Requirements
1. write text in the field and store it as a new note
2. click on a note and displat the taxt in the fields.
3. edit the field filled with text from a selected note
*/

export default function F({selectedNote}) {
  console.log(selectedNote)
  const [title, setTitle] = useState('')
  const onChangeTitle = (e) => setTitle(e.target.value)
  const onSave = (e) => {
    e.preventDefault()
    console.log('Saving new note:', title)
    createNote(title, '')
    console.log(getNotes())
  }
  return (
    <Form>
      <Form.Group controlId="formGroupTitle">
        <Form.Label>Title</Form.Label>
        <Form.Control //value={selectedNote ? selectedNote.title : title} onChange={onChangeTitle}
          value={title}
          onChange={onChangeTitle}
        />
      </Form.Group>
      <Button onClick={onSave} variant="secondary" style={{marginRight: '5px'}}>
        Save
      </Button>
    </Form>
  )
}

