import React from 'react'
import {getNotes} from '../utils/noteHelpers'
//import * as N from '../utils/noteHelpers'
import ReactuseState, {useState, useEffect, useContext} from 'react'
import Jumbotron from 'react-bootstrap/Jumbotron'
import 'bootstrap/dist/css/bootstrap.min.css'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button'
import List from './List'
import Editor from './Editor'
import Form from './Form'
import {NoteContext} from '../context/NoteContext'

export default function Notes() {
  const [selectedNote, setSelectedNote] = useState(undefined)
  //const [notes, setNotes] = useState([]) Behövs inte i o men context under
  const {notes, setNotes} = useContext(NoteContext)

  /* Behövs ej längre pga context
  useEffect(() => {
    const notes = getNotes()
    setNotes(notes)
  }, [])
  */

  const refreshList = () => {
    setSelectedNote(undefined)
    useContext(NoteContext)
    setNotes([...notes])
  }

  const onClickNewNote = () => setSelectedNote(undefined)

  return (
    <Container>
      <Jumbotron fluid>
        <Container>
          <h1>Notes by Angelica</h1>
          <p>Här är min note app!</p>
        </Container>
      </Jumbotron>
      <Row>
        <Col sm={4}>
          <>
            <Button onClick={onClickNewNote} variant="dark" size="md" block className="mb-4">
              New note
            </Button>
          </>
          <List notes={notes} selectedNote={selectedNote} setSelectedNote={setSelectedNote} />
        </Col>
        <Col>
          {/*<Editor />*/}
          <Form refreshList={refreshList} selectedNote={selectedNote} />
          {/*<Clock /> */}
        </Col>
      </Row>
    </Container>
  )
}
